This is my test case, it works.
( There is no ‘make_refptr_for_instance’ function in my Glib, so I constructor a Glib::RefPtr and return )

```c++
#include <giomm.h>
#include <glibmm.h>
#include <future>
#include <iostream>
#include <memory>
#include <mutex>
#include <string>
#include <vector>

namespace {
std::mutex wrap_mutex;
}  // anonymous namespace

namespace Glib {
Glib::RefPtr<Gio::DBus::Connection> wrap(GDBusConnection* object,
                                         bool take_copy) {
    std::cout << "wrap Connection " << std::endl;

    Glib::ObjectBase* pCppObject = nullptr;
    if (!ObjectBase::_get_current_wrapper((GObject*) object)) {
        // 'object' does not yet have a C++ wrapper. Construction of the C++
        // wrapper must be thread-safe. See comments at the definition of
        // wrap_mutex.
        std::lock_guard<std::mutex> lock(wrap_mutex);
        pCppObject = Glib::wrap_auto((GObject*) object, take_copy);
    } else
        pCppObject = Glib::wrap_auto((GObject*) object, take_copy);

    return Glib::RefPtr<Gio::DBus::Connection>(
            dynamic_cast<Gio::DBus::Connection*>(pCppObject));
    // We use dynamic_cast<> in case of multiple inheritance.
}
}  // namespace Glib

class TestConnection {
public:
    TestConnection(const std::string& name) : name_(name) {}
    ~TestConnection() {
        future_.get();
    }

    void Run() {
        future_ = std::async(std::launch::async, [this]() {
            // Instantiate and run the main loop of this thread
            auto context = Glib::MainContext::create();
            g_main_context_push_thread_default(context->gobj());
            main_loop_ = Glib::MainLoop::create(context, false);

            // 1. name_lost_slot with a nullptr connection (if a connection to
            // the bus can't be made).

            // 2. bus_acquired_slot then name_lost_slot (if the name can't be
            // obtained)

            // 3. bus_acquired_slot then name_acquired_slot (if the name was
            // obtained).

            auto connection_id = Gio::DBus::own_name(
                    Gio::DBus::BUS_TYPE_SYSTEM, name_,
                    sigc::mem_fun(this, &TestConnection::OnBusAcquired),
                    sigc::mem_fun(this, &TestConnection::OnNameAcquired),
                    sigc::mem_fun(this, &TestConnection::OnNameLost));
            std::cout << "run: " << name_ << std::endl;

            main_loop_->run();
            std::cout << "run finish: " << name_ << std::endl;

            main_loop_.reset();

            Gio::DBus::unown_name(connection_id);
            g_main_context_pop_thread_default(context.get()->gobj());
        });
    }

private:
    void OnBusAcquired(const Glib::RefPtr<Gio::DBus::Connection>&,
                       Glib::ustring) {
        std::cout << "OnBusAcquired: " << std::endl;
    }

    void OnNameAcquired(const Glib::RefPtr<Gio::DBus::Connection>&,
                        Glib::ustring) {
        std::cout << "OnNameAcquired: " << std::endl;
        main_loop_->quit();
    }

    void OnNameLost(const Glib::RefPtr<Gio::DBus::Connection>&, Glib::ustring) {
        std::cout << "OnNameLost: " << std::endl;
        main_loop_->quit();
    }

private:
    std::string name_;
    std::future<void> future_;
    Glib::RefPtr<Glib::MainLoop> main_loop_;
};

int main(int argc, char** argv) {
    Glib::init();
    Gio::init();

    std::vector<std::shared_ptr<TestConnection>> name_list = {
            std::make_shared<TestConnection>("com.test.name0"),
            std::make_shared<TestConnection>("com.test.name1"),
            std::make_shared<TestConnection>("com.test.name2"),
            std::make_shared<TestConnection>("com.test.name3"),
            std::make_shared<TestConnection>("com.test.name4"),
            std::make_shared<TestConnection>("com.test.name5"),
            std::make_shared<TestConnection>("com.test.name6"),
            std::make_shared<TestConnection>("com.test.name7"),
            std::make_shared<TestConnection>("com.test.name8"),
            std::make_shared<TestConnection>("com.test.name9"),
    };

    for (auto& connection : name_list) {
        connection->Run();
    }

    return 0;
}
